    <style>
        html,
        body {
            background-color: #F6F5F5;
            font-family: 'Be Vietnam Pro', sans-serif;
            font-weight: 300;
            height: 100vh;
            margin: 0;
        }

        a {
            text-decoration: none;
        }

        li {
            list-style-type: none;
        }



        .flex {
            display: flex;
        }

        .flex-inbetween {
            justify-content: space-between;
        }

        .flex-items-center {
            align-items: center;
        }

        .flex-wrap {
            flex-wrap: wrap;
        }

        .container {
            padding: 24px 32px;
        }


        .font-medium {
            font-weight: 500;
        }

        .font-24 {
            font-size: 24px;
        }

        .font-16 {
            font-size: 16px;
        }

        .login-btn {
            padding: 8px 32px;
            margin: 10px;
            border: 1px solid #E1DDDD80;
            border-radius: 6px;
            cursor: pointer;

        }

        .register-btn {
            padding: 8px 32px;
            background-color: #5EBBFF;
            border-radius: 6px;
            border: 1px solid #5EBBFF;
            cursor: pointer;

        }

        .logo {
            /* height: 100px;
            width: 200px; */
        }

        section {
            /* width: 80vw; */
            height: 100%;
            max-height: 78vh;

            /* padding: 50% 50%; */
            display: flex;
            justify-content: center;
            align-items: center;



        }

        .wrapper {
            width: 800px;
            height: 500px;
            text-align: center;
        }

        .main-wrapper {

            padding: 0% 0%;
            width: 100%;
            height: 100%;
            flex-flow: column nowrap;
            display: flex;
            justify-content: center;
            align-items: center;
        }

        .main-h {
            font-weight: 900;
            font-size: 65px;
            text-align: center;


        }

        .main-h>p {
            margin: 0 0;
        }


        .second-h {
            font-weight: 400;
            font-size: 35px;
            text-align: center;
            color: #141414;
            opacity: 40%;


        }

        .register-btn-hero {
            padding: 16px 48px;
            background-color: #5EBBFF;
            border-radius: 6px;
            border: 1px solid #5EBBFF;
            cursor: pointer;
            text-align: center;
            vertical-align: middle;
            display: inline-block;

        }

        .register-btn-hero:hover {
            background-color: #42acf7;
        }

        .register-btn-hero>svg {
            vertical-align: middle;
            display: inline-block;
        }

        .footer {
            background-color: #CCC5C5;
        }

    </style>
